import React from 'react'
import Box from '@mui/material/Box'
import {
  AppBar,
  Container,
  IconButton,
  Toolbar,
  Typography,
} from '@mui/material'
import HowToVoteIcon from '@mui/icons-material/HowToVote'
import { Link } from 'react-router-dom'

function Footer() {
  return (
    <footer>
      {/* 푸터의 AppBar 컴포넌트 */}
      <AppBar
        position="static"
        style={{
          background: '#2E3B55', // 배경색상을 헤더와 같은 색으로 설정
          top: 'auto', // 화면 상단에 고정되지 않도록
          bottom: 0, // 화면 하단에 고정
        }}
      >
        <Container>
          <Toolbar>
            {/* 왼쪽 아이콘 (HowToVoteIcon) */}
            <IconButton
              size="large"
              edge="start"
              color="inherit"
              aria-label="open drawer"
              sx={{ mr: 2 }}
            >
              <HowToVoteIcon />
            </IconButton>
            {/* 가운데 텍스트 (BlockVote) */}
            <Typography
              variant="h6"
              noWrap
              component={Link}
              to="/"
              style={{
                color: 'inherit',
                textDecoration: 'inherit',
              }}
              sx={{ flexGrow: 1 }}
            >
              BlockVote
            </Typography>
            {/* 오른쪽 링크 (Team Project PPT) */}
            <a
              href="https://o365joongbu-my.sharepoint.com/:p:/g/personal/yaejin02_21_jmail_ac_kr/EX1LyeRBGUpGgkINwM7EvewBtZrWAHZEMVqF-R-_5gv9kg?e=DjPwJ6"
              style={{ color: 'white' }}
            >
              Team Project PPT
            </a>
          </Toolbar>
        </Container>
      </AppBar>
    </footer>
  )
}

export default Footer
