import React from 'react'

import { AppBar, Container, Toolbar, Typography } from '@mui/material'
import { Link } from 'react-router-dom'

function Footer() {
  return (
    <footer>
      {/* 푸터의 AppBar 컴포넌트 */}
      <AppBar
        position="static"
        style={{
          background: '#2E3B55', // 배경색상을 헤더와 같은 색으로 설정
          top: 'auto', // 화면 상단에 고정되지 않도록
          bottom: 0, // 화면 하단에 고정
        }}
      >
        <Container>
          <Toolbar>
            {/* 가운데 텍스트 (BlockVote) */}
            <Typography
              variant="h6"
              noWrap
              component={Link}
              to="/"
              style={{
                color: 'inherit',
                textDecoration: 'inherit',
              }}
              sx={{ flexGrow: 1 }}
            >
              중부대학교
            </Typography>
            {/* 오른쪽 링크 (Team Project PPT) */}
            <Typography
              variant="h6"
              noWrap
              component={Link}
              to="/"
              style={{
                color: 'inherit',
                textDecoration: 'inherit',
              }}
              sx={{ flexGrow: 1 }}
            >
              정보보호학과
            </Typography>
          </Toolbar>
        </Container>
      </AppBar>
    </footer>
  )
}

export default Footer
